mapper.select:
SELECT BOARDSEQ, BOARDKIND, BOARDCATEGORY, TITLE, CONTENT, NOTICEYN, BOARDSTATUS, BOARDINDATE, BOARDINUSERID, BOARDUPDATE, BOARDUPUSERID, FILESEQ, SUBSEQ, USERID, LOLID, NICKNAME, BLACKSTACK, USERSTATUS, USERINDATE, USERUPDATE, USERINUSERID, USERUPUSERID, USERPOSITION, BROWNIEPOINT, REPLYCNT, HITCNT, LIKECNT, UNLIKECNT FROM (SELECT ROWNUM AS RN, BOARD.* FROM (SELECT BROWNIE_BOARD.BOARD_SEQ AS BOARDSEQ, BROWNIE_BOARD.BOARD_KIND AS BOARDKIND, BROWNIE_BOARD.BOARD_CATEGORY AS BOARDCATEGORY, BROWNIE_BOARD.TITLE AS TITLE, BROWNIE_BOARD.CONTENT AS CONTENT, BROWNIE_BOARD.NOTICE_YN AS NOTICEYN, BROWNIE_BOARD.STATUS AS BOARDSTATUS, BROWNIE_BOARD.IN_DATE AS BOARDINDATE, BROWNIE_BOARD.IN_USER_ID AS BOARDINUSERID, BROWNIE_BOARD.UP_DATE AS BOARDUPDATE, BROWNIE_BOARD.UP_USER_ID AS BOARDUPUSERID, BROWNIE_BOARD.FILE_SEQ AS FILESEQ, BROWNIE_BOARD.SUB_SEQ AS SUBSEQ, BROWNIE_USER.USER_ID AS USERID, BROWNIE_USER.LOL_ID AS LOLID, BROWNIE_USER.NICK_NAME AS NICKNAME, BROWNIE_USER.BLACK_STACK AS BLACKSTACK, BROWNIE_USER.STATUS AS USERSTATUS, BROWNIE_USER.IN_DATE AS USERINDATE, BROWNIE_USER.UP_DATE AS USERUPDATE, BROWNIE_USER.IN_USER_ID AS USERINUSERID, BROWNIE_USER.UP_USER_ID AS USERUPUSERID, BROWNIE_USER.USER_POSITION AS USERPOSITION, BROWNIE_USER.BROWNIE_POINT AS BROWNIEPOINT, NVL(BROWNIE_BOARD_REPLY.REPLY_CNT, 0) AS REPLYCNT, NVL(BROWNIE_BOARD_HIT.HIT_CNT, 0) AS HITCNT, NVL(BROWNIE_BOARD_LIKE.LIKE_CNT, 0) AS LIKECNT, NVL(BROWNIE_BOARD_LIKE.UNLIKE_CNT, 0) AS UNLIKECNT FROM (SELECT * FROM BROWNIE_BOARD WHERE 1 = 1
AND BROWNIE_BOARD.BOARD_SEQ = #{boardSeq}
AND UPPER(BROWNIE_BOARD.BOARD_KIND) = UPPER(#{boardKind})
AND UPPER(BROWNIE_BOARD.BOARD_CATEGORY) = UPPER(#{boardCategory})
AND UPPER(BROWNIE_BOARD.TITLE) LIKE UPPER('%'||#{title}||'%')
AND UPPER(BROWNIE_BOARD.CONTENT) LIKE UPPER('%'||#{content}||'%')
AND UPPER(BROWNIE_BOARD.NOTICE_YN) = UPPER(#{noticeYn})
AND UPPER(BROWNIE_BOARD.STATUS) = UPPER(#{boardStatus})
) BROWNIE_BOARD INNER JOIN (SELECT * FROM BROWNIE_USER WHERE 1 = 1
AND UPPER(NICK_NAME) = UPPER(#{nickName})
AND UPPER(USER_ID) = UPPER(#{userId})
) BROWNIE_USER ON BROWNIE_BOARD.IN_USER_ID = BROWNIE_USER.USER_ID LEFT OUTER JOIN (SELECT BOARD_SEQ, COUNT(*) AS REPLY_CNT FROM BROWNIE_BOARD_REPLY INNER JOIN BROWNIE_USER ON BROWNIE_BOARD_REPLY.IN_USER_ID = BROWNIE_USER.USER_ID GROUP BY BOARD_SEQ) BROWNIE_BOARD_REPLY ON BROWNIE_BOARD.BOARD_SEQ = BROWNIE_BOARD_REPLY.BOARD_SEQ LEFT OUTER JOIN (SELECT BOARD_SEQ, COUNT(*) AS HIT_CNT FROM BROWNIE_BOARD_HIT GROUP BY BOARD_SEQ) BROWNIE_BOARD_HIT ON BROWNIE_BOARD.BOARD_SEQ = BROWNIE_BOARD_HIT.BOARD_SEQ LEFT OUTER JOIN (SELECT BOARD_SEQ, LIKE_CNT, UNLIKE_CNT FROM (SELECT BOARD_SEQ, KIND FROM BROWNIE_BOARD_LIKE) PIVOT ( COUNT(*) FOR KIND IN (1 AS LIKE_CNT, 0 AS UNLIKE_CNT))) BROWNIE_BOARD_LIKE ON BROWNIE_BOARD.BOARD_SEQ = BROWNIE_BOARD_LIKE.BOARD_SEQ ORDER BY BROWNIE_BOARD.BOARD_SEQ DESC ) BOARD )
WHERE ${contentPerPage} * (${pageNum} - 1) < RN AND RN <= ${contentPerPage} * ${pageNum}

SELECT BOARDSEQ, BOARDKIND, BOARDCATEGORY, TITLE, CONTENT, NOTICEYN, BOARDSTATUS, BOARDINDATE, BOARDINUSERID, BOARDUPDATE, BOARDUPUSERID, FILESEQ, SUBSEQ, USERID, LOLID, NICKNAME, BLACKSTACK, USERSTATUS, USERINDATE, USERUPDATE, USERINUSERID, USERUPUSERID, USERPOSITION, BROWNIEPOINT, REPLYCNT, HITCNT, LIKECNT, UNLIKECNT FROM (SELECT ROWNUM AS RN, BOARD.* FROM (SELECT BROWNIE_BOARD.BOARD_SEQ AS BOARDSEQ, BROWNIE_BOARD.BOARD_KIND AS BOARDKIND, BROWNIE_BOARD.BOARD_CATEGORY AS BOARDCATEGORY, BROWNIE_BOARD.TITLE AS TITLE, BROWNIE_BOARD.CONTENT AS CONTENT, BROWNIE_BOARD.NOTICE_YN AS NOTICEYN, BROWNIE_BOARD.STATUS AS BOARDSTATUS, BROWNIE_BOARD.IN_DATE AS BOARDINDATE, BROWNIE_BOARD.IN_USER_ID AS BOARDINUSERID, BROWNIE_BOARD.UP_DATE AS BOARDUPDATE, BROWNIE_BOARD.UP_USER_ID AS BOARDUPUSERID, BROWNIE_BOARD.FILE_SEQ AS FILESEQ, BROWNIE_BOARD.SUB_SEQ AS SUBSEQ, BROWNIE_USER.USER_ID AS USERID, BROWNIE_USER.LOL_ID AS LOLID, BROWNIE_USER.NICK_NAME AS NICKNAME, BROWNIE_USER.BLACK_STACK AS BLACKSTACK, BROWNIE_USER.STATUS AS USERSTATUS, BROWNIE_USER.IN_DATE AS USERINDATE, BROWNIE_USER.UP_DATE AS USERUPDATE, BROWNIE_USER.IN_USER_ID AS USERINUSERID, BROWNIE_USER.UP_USER_ID AS USERUPUSERID, BROWNIE_USER.USER_POSITION AS USERPOSITION, BROWNIE_USER.BROWNIE_POINT AS BROWNIEPOINT, NVL(REPLY.REPLY_CNT, 0) AS REPLYCNT, NVL(HIT.HIT_CNT, 0) AS HITCNT, NVL(LIKE_BOARD.LIKE_CNT, 0) AS LIKECNT, NVL(LIKE_BOARD.UNLIKE_CNT, 0) AS UNLIKECNT FROM (SELECT * FROM BROWNIE_BOARD WHERE 1 = 1 AND BROWNIE_BOARD.BOARD_SEQ = #{boardSeq}
AND UPPER(BROWNIE_BOARD.BOARD_KIND) = UPPER(#{boardKind})
) BROWNIE_BOARD INNER JOIN (SELECT * FROM BROWNIE_USER ) BROWNIE_USER ON BROWNIE_BOARD.IN_USER_ID = BROWNIE_USER.USER_ID LEFT OUTER JOIN (SELECT BOARD_SEQ, COUNT(*) AS REPLY_CNT FROM BROWNIE_BOARD_REPLY INNER JOIN BROWNIE_USER ON BROWNIE_BOARD_REPLY.IN_USER_ID = BROWNIE_USER.USER_ID GROUP BY BOARD_SEQ) REPLY ON BROWNIE_BOARD.BOARD_SEQ = REPLY.BOARD_SEQ LEFT OUTER JOIN (SELECT BOARD_SEQ, COUNT(*) AS HIT_CNT FROM BROWNIE_BOARD_HIT GROUP BY BOARD_SEQ) HIT ON BROWNIE_BOARD.BOARD_SEQ = HIT.BOARD_SEQ LEFT OUTER JOIN (SELECT BOARD_SEQ, UNLIKE_CNT, LIKE_CNT FROM (SELECT BOARD_SEQ, KIND FROM BROWNIE_BOARD_LIKE) PIVOT ( COUNT(*) FOR KIND IN (0 AS UNLIKE_CNT, 1 AS LIKE_CNT))) LIKE_BOARD ON BROWNIE_BOARD.BOARD_SEQ = LIKE_BOARD.BOARD_SEQ ORDER BY BROWNIE_BOARD.BOARD_SEQ DESC ) BOARD )
WHERE ${contentPerPage} * (${pageNum} - 1) < RN AND RN <= ${contentPerPage} * ${pageNum}

mapper.update:

UPDATE BROWNIE_BOARD
SET UP_USER_ID     = #{userId}
  , UP_DATE        = SYSDATE



  , BOARD_KIND     = #{boardKind}




  , BOARD_CATEGORY = #{boardCategory}




  , TITLE = #{title}




  , CONTENT = #{content}




  , NOTICE_YN = #{noticeYn}




  , STATUS = #{status}




  , FILE_SEQ = #{fileSeq}




  , SUB_SEQ = #{subSeq}



WHERE BOARD_SEQ = #{boardSeq}
