mapper.insert:
INSERT ALL
    INTO TEAMGAME_POSITION ( POSITION_SEQ
                           , TOP
                           , MID
                           , JUN
                           , BOT
                           , SUP)
VALUES ( POSITION_SEQ.NEXTVAL
       , #{top}
       , #{mid}
       , #{jun}
       , #{bot}
       , #{sup})
INTO TEAMGAME ( TEAMGAME_SEQ
              , USER_ID
              , TITLE
              , MATCH_MODE
              , MESSAGE
              , POSITION_SEQ
              , POSITION
              , STATUS
              , LEADER
              , MIKE
              , USE_POINT
              , IN_DATE
              , IN_USER_ID
              , UP_DATE
              , UP_USER_ID)
VALUES ( TEAMGAME_SEQ.NEXTVAL
       , #{userId}
       , #{title}
       , #{matchMode}
       , #{message}
       , POSITION_SEQ.CURRVAL
       , #{position}
       , #{status}
       , #{leader}
       , #{mike}
       , #{usePoint}
       , SYSDATE
       , #{userId}
       , SYSDATE
       , #{userId})
SELECT *
FROM DUAL

    SELECT TEAMGAME_SEQ.CURRVAL from dual

mapper.select:
select TEAMGAME_SEQ as teamGameSeq
      ,STATUS as status
      ,USER_ID as userId
from TEAMGAME
where TEAMGAME_SEQ = #{teamgameSeq}
and USER_ID = #{userId}

mapper.update:
update TEAMGAME
    set STATUS = #{status}
where TEAMGAME_SEQ = #{teamgameSeq}
  and USER_ID = #{userId}
