mapper.select:
SELECT NICK_NAME AS NICKNAME
FROM BROWNIE_USER
WHERE USER_ID = #{id}

mapper.update:
UPDATE BROWNIE_USER
SET BLACK_STACK = BLACK_STACK + 1
WHERE USER_ID = #{userId}

UPDATE BROWNIE_USER
SET IMAGE = #{image}
WHERE USER_ID = #{userId}

update BROWNIE_USER set
     BROWNIE_POINT = BROWNIE_POINT + #{browniePoint}
    ,UP_DATE = sysdate
    ,UP_USER_ID = #{userId}
WHERE USER_ID = #{userId}

update BROWNIE_USER set
     BROWNIE_POINT = BROWNIE_POINT - #{browniePoint}
    ,UP_DATE = sysdate
    ,UP_USER_ID = #{userId}
WHERE USER_ID = #{userId}

UPDATE BROWNIE_USER
SET TOKEN = #{token}
WHERE USER_ID = #{userId}

UPDATE BROWNIE_USER
SET LOL_ID = #{lolId}
WHERE USER_ID = #{userId}
